mapper.update:
UPDATE adopt_pro
SET ani_hit = ani_hit + 1
WHERE ani_no = #{aniNo}

UPDATE adopt_pro
SET ani_cate = #{aniCate}
	, ani_name = #{aniName}
	, ani_age = #{aniAge}, ani_gender = #{aniGender}
	, ani_neutral = #{aniNeutral}, ani_content = #{aniContent}
	, ani_weight = #{aniWeight}
WHERE ani_no = #{aniNo}
