mapper.select:
SELECT * FROM ( SELECT rownum rnum, R.* FROM ( SELECT a.rept_no, adminid, rept_title, rept_content , rept_date, rfile_no, rfile_origin, rfile_stored FROM report a LEFT OUTER JOIN rept_file b ON a.rept_no = b.rept_no
WHERE rept_title LIKE '%' || #{reptTitle} || '%'
ORDER BY rept_no DESC ) R ) REPORT WHERE rnum BETWEEN #{paging.startNo } AND #{paging.endNo }

mapper.update:
UPDATE report
SET rept_title = #{reptTitle}
	, rept_content = #{reptContent}
WHERE rept_no = #{reptNo}
